import React from 'react'

export default function Options(props) {

    let selectedButton = props.selection.selectedAnswer === props.option ? {backgroundColor: "#D6DBF5", border: "none"} : {backgroundColor: 'transparent'}

    return (
        <div className="option">
            <button style={selectedButton} onClick={props.handleSelect}>{props.option}</button>
        </div>
    )
}
