import React from 'react'
import Option from './Option'

export default function Questions(props) {

    //States
    const [selection, setSelection] = React.useState({
        rightAnswer: props.correctAnswer, 
        wrongAnswers: props.incorrectAnswers,
        question: props.question,
        selectedAnswer: ''
    })
    const [shuffledArray, setShuffledArray] = React.useState([])
    //End of States

    let optionsArray = [props.correctAnswer, ...props.incorrectAnswers]

    //Shuffling function
    React.useEffect(() => {
      setShuffledArray(optionsArray)  
    }, [])

    function shuffleArray(array) {
        for (var i = array.length - 1; i > 0; i--) {

            // Generate random number
            var j = Math.floor(Math.random() * (i + 1));

            var temp = array[i];
            array[i] = array[j];
            array[j] = temp;
        }

        return array;
     }
     //End of Shuffling 

     //Render Options

     let options = shuffledArray.map(item => {
        return (
            <Option 
                key={item}
                option={item}
                handleSelect={select}
                selection={selection}
            />
        ) 
     })

     //End of rendering Options

     //Option Selection Event

     function select(event) {
        setSelection(prevSelection => {
            return {
                ...prevSelection,
                selectedAnswer: event.handle.innerText
            }
        })
     }
     //End of Option Selection Event

    return (
        <div className="question">
            <h2>{props.question}</h2>
            <div className="optionsContainer">{options}</div>
        </div>
    )
}
