import React from 'react'
import Question from './Question'
import topImage from '../images/shape-1.png'
import botomImage from '..images/shape-2.png'

export default function Quiz(props) {

    //Making API call here
    const [questions, setQuestions] = React.useState([])

    React.useEffect(() => {
        fetch("https://opentdb.com/api.php?amount=5&category=30&type=multiple")
            .then(response => response.json())
            .then(data => setQuestions(data.results))
    }, [])
    //End of making API Call here

    //Creating a list of Questions
    let questionsList = questions.map(item => {
        return (
            <Question 
                question={item.question}
                correctAnswer={item.correct_answer}
                incorrectAnswers={item.incorrect_answers}
            />
        )
    })
    //End of creating a list of Questions

    return (
        <div className="quiz-container">
            <img className="topImage" src={topImage}></img>
            <div>{questionsList}</div>
            <img className="bottomImage" src={bottomImage}></img>
        </div>
    )
}
